import React from 'react';
import {
  createStyles,
  Card,
  Overlay,
  CardProps,
  Button,
  Text,
  useMantineTheme,
} from '@mantine/core';

const useStyles = createStyles((theme) => ({
  card: {
    height: 240,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },

  content: {
    position: 'absolute',
    padding: theme.spacing.xl,
    zIndex: 1,
    top: 0,
    bottom: 0,
    right: 0,
    left: 0,
  },

  action: {
    position: 'absolute',
    bottom: theme.spacing.xl,
    right: theme.spacing.xl,
  },

  title: {
    color: theme.white,
    marginBottom: theme.spacing.xs / 2,
  },

  description: {
    color: theme.white,
    maxWidth: 220,
  },
}));

interface ImageActionBannerProps {
  title: React.ReactNode;
  description: React.ReactNode;
  image: string;
  action: {
    label: string;
    link: string;
  };
}

export function ImageActionBanner({
  title,
  description,
  image,
  action,
  style,
  className,
  ...others
}: ImageActionBannerProps & Omit<CardProps<'div'>, keyof ImageActionBannerProps | 'children'>) {
  const { classes, cx } = useStyles();
  const theme = useMantineTheme();

  return (
    <Card
      radius="md"
      style={{ backgroundImage: `url(${image})`, ...style }}
      className={cx(classes.card, className)}
      {...others}
    >
      <Overlay
        gradient={`linear-gradient(105deg, ${theme.black} 20%, #312f2f 50%, ${theme.colors.gray[4]} 100%)`}
        opacity={0.55}
        zIndex={0}
      />

      <div className={classes.content}>
        <Text size="lg" weight={700} className={classes.title}>
          {title}
        </Text>

        <Text size="sm" className={classes.description}>
          {description}
        </Text>

        <Button
          className={classes.action}
          variant="white"
          color="dark"
          component="a"
          size="xs"
          href={action.link}
        >
          {action.label}
        </Button>
      </div>
    </Card>
  );
}

import React from 'react';
import { Image, Text, Title, Button, createStyles } from '@mantine/core';

const useStyles = createStyles((theme) => ({
  wrapper: {
    display: 'flex',
    alignItems: 'center',
    borderRadius: theme.radius.md,
    padding: theme.spacing.md,
    backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[6] : theme.white,
    border: `1px solid ${
      theme.colorScheme === 'dark' ? theme.colors.dark[6] : theme.colors.gray[3]
    }`,

    [`@media (max-width: ${theme.breakpoints.sm}px)`]: {
      flexDirection: 'column',
    },
  },

  header: {
    textTransform: 'uppercase',
    fontSize: theme.fontSizes.sm,
    fontWeight: 700,
    marginBottom: theme.spacing.lg,
  },

  body: {
    flex: '0 0 60%',
    marginLeft: theme.spacing.xl * 2,

    [`@media (max-width: ${theme.breakpoints.sm}px)`]: {
      flex: '0 0 100%',
      marginLeft: 0,
      marginTop: theme.spacing.xl,
    },
  },

  control: {
    [`@media (max-width: ${theme.breakpoints.sm}px)`]: {
      width: '100%',
    },
  },

  title: {
    fontSize: 52,
    color: theme.colorScheme === 'dark' ? theme.white : theme.black,
    fontFamily: `Greycliff CF, ${theme.fontFamily}`,
    marginBottom: theme.spacing.lg,
    fontWeight: 900,
    lineHeight: 0.9,

    [`@media (max-width: ${theme.breakpoints.sm}px)`]: {
      fontSize: 32,
      lineHeight: 1.1,
    },
  },
}));

interface ImageBannerProps {
  image: string;
  header: string;
  title: string;
  action: string;
}

export function ImageBanner({ image, header, title, action }: ImageBannerProps) {
  const { classes } = useStyles();
  return (
    <div className={classes.wrapper}>
      <Image src={image} radius="md" />
      <div className={classes.body}>
        <Text color="dimmed" className={classes.header}>
          {header}
        </Text>
        <Title order={2} className={classes.title}>
          {title}
        </Title>
        <Button variant="light" size="md" className={classes.control}>
          {action}
        </Button>
      </div>
    </div>
  );
}
